import { useState } from "react";

const images = [
    "https://images.pexels.com/photos/3836292/pexels-photo-3836292.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500",
    "https://images.pexels.com/photos/2792157/pexels-photo-2792157.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500",
    "https://images.pexels.com/photos/1166209/pexels-photo-1166209.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500",
    "https://images.pexels.com/photos/1661179/pexels-photo-1661179.jpeg?auto=compress&cs=tinysrgb&dpr=2&w=500",

];

export const Accordian = () => {
    const [activeIndex, setActiveIndex] = useState(0);

    return (
        <div>
            <div style={{
                width: '3%',
                backgroundColor: 'black',
                padding:'5px',
                marginTop:'20%',
                marginLeft:'80px',
                borderRadius:'20px'
            }}
            ><button style={{
                color: 'white',
                width:'20px',
                padding:'3px',
                marginTop:'px'
                }} onClick={() => setActiveIndex(activeIndex === 0 ? images.length - 1 : activeIndex - 1)}></button>
            </div>
            {images.map((current, index) =>
                activeIndex === index && (
                    <div  style={{ width:'90%',height:'500px',
                                  marginTop:'-20%' }}key={current}><img  style={{ width: '60%',height:'500px' }} src={current} /></div>))
            }
            <div style={{width:'4%',backgroundColor:'black',marginRight:'50%'}}> <button style={{}} onClick={() => setActiveIndex(activeIndex === images.length - 1 ? 0 : activeIndex + 1)}></button>
            </div></div>
    );
}
