import { useState } from "react"

const numbers=[
    'ramesh',
    'suresh',
    'naresh',
    'katesh'
]
export const Exam=()=>{
    const [current,setCurrent]=useState(0);
    return(
     <div>
        <div>
            <button onClick={()=>{setCurrent(current===0? numbers.length-1:-1)}}>previous</button>
        </div>
        {numbers.map((number,index)=>
        current===index && (<div key={number}>{number}</div>))}
        <div><button>next</button></div>
     </div>
    )
}
